mapper.select:
SELECT * FROM (SELECT rownum rnum, qna_num, member_id, qna_type,
 qna_title, qna_content, qna_date, qna_file, qna_check, qna_scr, qna_pass FROM (SELECT * FROM QNA ORDER BY QNA_NUM desc ))
 WHERE rnum>=#{arg0} and rnum<=#{arg1}

SELECT * FROM (SELECT rownum rnum, QNA_NUM, MEMBER_ID, QNA_TYPE, ORDER_NUM,
 QNA_TITLE, QNA_CONTENT, QNA_DATE, QNA_FILE, QNA_CHECK FROM (SELECT * FROM QNA ORDER BY QNA_NUM desc))
 WHERE rnum>=#{startrow} and rnum<=#{endrow} and member_id = #{member_id}
